	/**
	 *  图片转二进制字符串
	 * @param imgPath  图片路径，包含图片名称和扩展名
	 * @return
	 * @throws Exception
	 */
	public static String imagToHexStr(String imgPath) throws Exception{
        FileInputStream fis = new FileInputStream(imgPath);  
        java.io.ByteArrayOutputStream bos = new java.io.ByteArrayOutputStream();  
        byte[] buff = new byte[1024];  
        int len = 0;  
        while ((len = fis.read(buff)) != -1)  
        {  
            bos.write(buff, 0, len);  
        }  
        // 得到图片的字节数组   
        byte[] result = bos.toByteArray();  
        // 字节数组转成十六进制   
        String str = ByteUtils.toHexString(result);
        return str;
	}
	
	public static String imagToHexStr(File imgFile) throws Exception{
        FileInputStream fis = new FileInputStream(imgFile);  
        java.io.ByteArrayOutputStream bos = new java.io.ByteArrayOutputStream();  
        byte[] buff = new byte[1024];  
        int len = 0;  
        while ((len = fis.read(buff)) != -1)  
        {  
            bos.write(buff, 0, len);  
        }  
        // 得到图片的字节数组   
        byte[] result = bos.toByteArray();  
        // 字节数组转成十六进制   
        String str = ByteUtils.toHexString(result);
        return str;
	}
	
	/**
	 * 二进制字符串转图片
	 * @param hexStr 二进制字符串
	 * @param filePath 图片存储路径，包含文件名和扩展名
	 * @throws Exception
	 */
	public static void hexStringToImg(String hexStr,String filePath) throws Exception{
		 BufferedImage image = new BufferedImage(300, 300,BufferedImage.TYPE_BYTE_BINARY);  
		    ByteArrayOutputStream byteOutputStream = new ByteArrayOutputStream();  
		    try {  
		        ImageIO.write(image, "jpg", byteOutputStream);  
		        byte[] bytes =ByteUtils.toBytes(hexStr);  
		        RandomAccessFile file = new RandomAccessFile(filePath, "rw");  
		        file.write(bytes);  
		        file.close();  
		    } catch (IOException e) {  
		        e.printStackTrace();  
		    }  
		
	}
	
	
